//@ts-ignore

import React, { useState, useMemo, useEffect } from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
import dayjs from 'dayjs'
import ReactMapGL, { Marker, Popup, StaticMap } from 'react-map-gl';
import { getPopulation } from '../assests/countries'
import Chart from '../components/Chart'
import codes from '../assests/codes'
interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
    lat?: number;
    long?: number;
}

interface IProps {
    data: API
}

const radius = (opa) => {
    let op = (opa) / 200

    if (op > 100) {
        op = 100
    }
    if (op < 20) {
        op *= 3
    }
    return op
}

const opacity = (opa) => {
    let op = (opa) / 200
    if (op < 0.3) {
        op = 0.3
    }
    return op
}

const MAPBOX_TOKEN = 'pk.eyJ1IjoibWFydGlzOTAwIiwiYSI6ImNrMTZtazN4ODBhNGUzbW1yc245dG9nZzYifQ.T2ImKPTOuImP5pW11SXoWg';

function HomePage({ data }: IProps) {
    const [showPopup, setShowPopup]: any = useState({ visible: false, location: null })

    const [viewport, setViewport] = useState({
        width: 400,
        height: 400,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8
    });

    return (
        <div style={{ width: '100vh' }}>
            <Layout>
                <div style={{ padding: 20, color: 'white', flexDirection: 'row', display: 'flex', height: '100%' }}>
                    <div style={{ display: 'flex', position: 'relative', height: 'calc(100vh - 152px)' }}>
                        {showPopup.visible === true && <LocationDetails showPopup={showPopup} />}

                        <ReactMapGL
                            {...viewport}
                            width="80vw"
                            height='calc(100vh - 152px)'
                            mapStyle="mapbox://styles/mapbox/dark-v9"
                            onViewportChange={setViewport}
                            mapboxApiAccessToken={MAPBOX_TOKEN}
                            onClick={() => setShowPopup({ visible: false, location: null })}
                        >
                            <RenderCountries data={data} showPopup={showPopup} setShowPopup={setShowPopup} />
                            <RenderCities data={data} showPopup={showPopup} setShowPopup={setShowPopup} />
                        </ReactMapGL>
                    </div>
                </div>
            </Layout>
            <div style={{ height: 'calc(100vh - 172px)', width: '100%', padding: 20, display: 'flex', flexDirection: 'column' }}>
                <p>LAST UPDATED - <b>{dayjs(data?.lastUpdated).format('YYYY-MM-DD HH:mm:ss')}</b></p>
                <ol style={{ margin: 0, padding: 0, height: '100%', overflowY: 'scroll', scrollBehavior: 'smooth', display: 'flex', flexDirection: 'column' }}>
                    {
                        data.areas.slice(0, 10).map((area: API) => (
                            // <li key={area.displayName} style={{ marginBottom: 25, display: 'flex', flexDirection: 'column', background: 'rgb(45,51,70)', padding: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto', }}>
                            //     <h1 style={{ flex: 2, color: 'white' }}>{area.displayName}</h1>
                            //     <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-around' }}>
                            //         <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 20 }}><b>Confirmed:</b> <br /> {area.totalConfirmed?.toLocaleString('en-US') ?? 0}</p>
                            //         <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 20 }}><b>Recovered:</b> <br />{area.totalRecovered?.toLocaleString('en-US') ?? 0}</p>
                            //         <p style={{ flex: 1, color: 'red', fontSize: 20 }}><b>Deaths:</b> <br />{area.totalDeaths?.toLocaleString('en-US') ?? 0}</p>
                            //     </div>
                            // </li>
                            <li style={{ marginBottom: 15, display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
                                <div style={{ borderRadius: '50%', width: 25, height: 25, overflow: 'hidden', display: 'flex', background: 'red', justifyContent: 'center', alignItems: 'center' }}>
                                    <img src={`https://www.countryflags.io/${codes(area.displayName) || 'us'}/shiny/64.png`} alt="123" style={{ width: '160%', height: '160%', objectFit: 'cover', borderRadius: '50%' }} />
                                </div>
                                <h5 style={{ paddingLeft: 15, color: 'grey', margin: '0 auto 0 0' }}>{area.displayName}</h5>
                                <h5>{area.totalConfirmed}</h5>
                            </li>
                        ))
                    }
                </ol>
            </div>
        </div>
    )
}

const LocationDetails = ({ showPopup }: any) => (
    <div style={{
        width: 400,
        height: 200,
        position: 'absolute',
        top: 25,
        left: 25,
        backgroundColor: 'white',
        zIndex: 99999999,
        padding: 15,
        borderRadius: 8
    }}>
        {showPopup.country === true ? <h3 style={{ color: 'black' }}>{showPopup.region.displayName}</h3> : <span style={{ color: 'black', display: 'flex', flexDirection: 'row', alignItems: 'center' }}><h3>{showPopup.region.displayName} </h3> <label style={{ paddingLeft: 8 }}>{showPopup.country}</label></span>}
        {showPopup.country === true && <p style={{ flex: 1, color: 'black', fontSize: 12 }}><b>Population:</b>  {getPopulation(showPopup.region.displayName).toLocaleString('en-US')}</p>}
        {showPopup.country === true && <h3 style={{ flex: 1, color: 'black', fontSize: 12 }}><b>Population:</b>  {Number((showPopup.region.totalConfirmed * 100) / getPopulation(showPopup.region.displayName)).toFixed(2)}%</h3>}
        <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 12 }}><b>Confirmed:</b>  {showPopup.region.totalConfirmed?.toLocaleString('en-US') ?? 0}</p>
        <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 12 }}><b>Recovered:</b>{showPopup.region.totalRecovered?.toLocaleString('en-US') ?? 0}</p>
        <p style={{ flex: 1, color: 'red', fontSize: 12 }}><b>Deaths:</b> {showPopup.region.totalDeaths?.toLocaleString('en-US') ?? 0}</p>
    </div>
)

const RenderCountries = ({ data, showPopup, setShowPopup }: { data: any, showPopup?: any, setShowPopup?: any }) => {
    return useMemo(() => {
        return data.areas.map(region => {
            return <RedBubbleMarker key={region.displayName} width={10} region={region} onClick={() => setShowPopup({ visible: true, region, country: true })} selected={region.displayName === showPopup.region?.displayName} />
        })
    }, [data, showPopup])
}

const RenderCities = ({ data, showPopup, setShowPopup }: { data: any, showPopup?: any, setShowPopup?: any }) => {
    return useMemo(() => {
        return data.areas.map(c => {
            return c.areas.map(region => {
                return <RedBubbleMarker key={region.displayName} width={5} region={region} onClick={() => setShowPopup({ visible: true, region, country: c.displayName })} selected={region.displayName === showPopup.region?.displayName} />

            })
        })
    }, [data, showPopup])
}



const RedBubbleMarker = ({ width, region, onClick, selected }: { width: number, region: API, onClick: () => void, selected: boolean }) => (
    <Marker key={region.displayName} longitude={region.long} latitude={region.lat} captureDrag={false}>
        <svg onClick={onClick}
            style={{
                cursor: 'pointer',
                fill: '#d00',
                stroke: 'none',
                width: (width * 2) + 10,
                height: (width * 2) + 10,
                transform: `translate(${-width / 2}px,${-width}px)`
            }}>
            <circle cx={width + 5} cy={width + 5} r={width} fill={"red"} strokeWidth={selected ? 3 : 0} stroke={selected ? "orange" : "red"} style={{ fillOpacity: opacity(region.totalDeaths) }} />
        </svg>
    </Marker>
)

export async function getStaticProps() {
