import React, { useState, useMemo, useEffect } from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
import dayjs from 'dayjs'
import ReactMapGL, { Marker, Popup } from 'react-map-gl';

interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
    lat?: number;
    long?: number;
}

interface IProps {
    data: API
}

const MAPBOX_TOKEN = 'pk.eyJ1IjoibWFydGlzOTAwIiwiYSI6ImNrMTZtazN4ODBhNGUzbW1yc245dG9nZzYifQ.T2ImKPTOuImP5pW11SXoWg';

function HomePage({ data }: IProps) {
    const [showPopup, setShowPopup]: any = useState({ visible: false, location: null })

    const [country, showCities] = useState(null)
    const [viewport, setViewport] = useState({
        width: 400,
        height: 400,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8
    });

    const radius = (opa) => {
        let op = (opa) / 200

        if (op > 100) {
            op = 100
        }
        if (op < 20) {
            op *= 3
        }
        return op
    }

    const opacity = (opa) => {
        let op = (opa) / 200
        if (op < 0.3) {
            op = 0.3
        }
        return op
    }

    return (
        <Layout>
            <div style={{ padding: 20, color: 'white', flexDirection: 'row', display: 'flex', height: '100%' }}>
                <div style={{ height: 'calc(100vh - 180px)', display: 'flex', flexDirection: 'column', overflowY: 'scroll', width: 480, scrollBehavior: 'smooth', scrollbarColor: 'transparent' }}>
                    <p>LAST UPDATED - <b>{dayjs(data.lastUpdated).format('YYYY-MM-DD HH:mm:ss')}</b></p>
                    <ul style={{ listStyleType: 'none', padding: 0, margin: 0 }}>
                        {
                            data.areas.map((area: API) => (
                                <li style={{ marginBottom: 25, display: 'flex', flexDirection: 'column', background: 'rgb(45,51,70)', padding: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto', }}>
                                    <h1 style={{ flex: 2, color: 'white' }}>{area.displayName}</h1>
                                    {/* <button onClick={() => showCities(area.displayName)}>show Cities</button> */}
                                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-around' }}>
                                        <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 20 }}><b>Confirmed:</b> <br /> {area.totalConfirmed?.toLocaleString('en-US') ?? 0}</p>
                                        <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 20 }}><b>Recovered:</b> <br />{area.totalRecovered?.toLocaleString('en-US') ?? 0}</p>
                                        <p style={{ flex: 1, color: 'red', fontSize: 20 }}><b>Deaths:</b> <br />{area.totalDeaths?.toLocaleString('en-US') ?? 0}</p>
                                    </div>
                                </li>
                            ))
                        }
                    </ul>
                </div>

                <div style={{ display: 'flex', width: '100%' }}>
                    <ReactMapGL
                        {...viewport}
                        width="100%"
                        height="calc(100vh - 120px)"
                        mapStyle="mapbox://styles/mapbox/dark-v9"
                        onViewportChange={setViewport}
                        mapboxApiAccessToken={MAPBOX_TOKEN}
                    >

                        {showPopup.visible === true && <Popup
                            latitude={showPopup.location.lat}
                            longitude={showPopup.location.long}
                            closeButton={true}
                            closeOnClick={false}
                            onClose={() => setShowPopup({ visible: false, location: null })}
                            anchor="top" >
                            <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 20 }}><b>Confirmed:</b>  {showPopup.region.totalConfirmed?.toLocaleString('en-US') ?? 0}</p>
                            <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 20 }}><b>Recovered:</b>{showPopup.region.totalRecovered?.toLocaleString('en-US') ?? 0}</p>
                            <p style={{ flex: 1, color: 'red', fontSize: 20 }}><b>Deaths:</b> {showPopup.region.totalDeaths?.toLocaleString('en-US') ?? 0}</p>
                        </Popup>}

                        {useMemo(() => {
                            return data.areas.map(region => {
                                return <CustomMarker />
                            })
                        }, [data])}

                        {/* {!!country && data.areas.filter(item => item.displayName === country)[0]?.areas.map(region => {
                            return <Marker key={region.displayName} longitude={region.long} latitude={region.lat}>
                                <svg onClick={() => setShowPopup({ visible: true, location: { lat: region.lat, long: region.long } })} >
                                    <circle cx={10} cy={10} r={10} fill="red" style={{ fillOpacity: opacity(region.totalDeaths) }} />
                                </svg>
                            </Marker>
                        })} */}

                        {useMemo(() => {
                            return data.areas.map(c => {
                                return c.areas.map(region => {
                                    return <Marker key={region.displayName} longitude={region.long} latitude={region.lat} captureDrag={false}>
                                        <svg>
                                            <circle cx={Number(radius(region.totalDeaths)) * 2} cy={Number(radius(region.totalDeaths)) * 2} r={Number(radius(region.totalDeaths))} fill="red" style={{ fillOpacity: opacity(region.totalDeaths) }} />
                                        </svg>
                                    </Marker>
                                })
                            })
                        }, [data])}
                    </ReactMapGL>
                </div>
            </div>
        </Layout >
    )
}

const CustomMarker = () => {
    return <Marker style={{ backgroundColor: 'green', width: 10 }} key={region.displayName} longitude={region.long} latitude={region.lat} captureDrag={false}>
        <svg onClick={() => setShowPopup({ visible: true, location: { long: region.long, lat: region.lat }, region })} style={{
            cursor: 'pointer',
            fill: '#d00',
            stroke: 'none',
            width: 10 * 2,
            height: 10 * 2,
            transform: `translate(${-10 / 2}px,${-10}px)`
        }}>
            <circle cx={10} cy={10} r={10} fill="red" style={{ fillOpacity: opacity(region.totalDeaths) }} />
        </svg>
    </Marker>
}

export async function getStaticProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()
    return { props: { data } }
}

export default React.memo(HomePage)
