import React, { useState, useEffect } from 'react'
import fetch from 'isomorphic-unfetch';

interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
}

interface IProps {
    data: API
}

function HomePage({ data }: IProps) {
    debugger
    return (
        <div>
            {data.areas.map((area: API) => (
                <div style={{ background: 'grey', padding: 20, margin: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto' }}>
                    <h3>{area.displayName}</h3>
                    <p><b>Confirmed</b> {area.totalConfirmed}</p>
                </div>
            ))}
        </div>
    )
}

export async function getServerSideProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()

    return { props: { data } }
}

export default HomePage
