import React, { useState, useMemo, useEffect } from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
import dayjs from 'dayjs'
import ReactMapGL, { Marker, Popup, StaticMap } from 'react-map-gl';
import { getPopulation } from '../assests/countries'
interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
    lat?: number;
    long?: number;
}

interface IProps {
    data: API
}

const MAPBOX_TOKEN = 'pk.eyJ1IjoibWFydGlzOTAwIiwiYSI6ImNrMTZtazN4ODBhNGUzbW1yc245dG9nZzYifQ.T2ImKPTOuImP5pW11SXoWg';

function HomePage({ data }: IProps) {
    const [showPopup, setShowPopup]: any = useState({ visible: false, location: null })

    const [country, showCities] = useState(null)
    const [viewport, setViewport] = useState({
        width: 400,
        height: 400,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8
    });

    const radius = (opa) => {
        let op = (opa) / 200

        if (op > 100) {
            op = 100
        }
        if (op < 20) {
            op *= 3
        }
        return op
    }

    const opacity = (opa) => {
        let op = (opa) / 200
        if (op < 0.3) {
            op = 0.3
        }
        return op
    }

    return (
        <Layout>
            <div style={{ padding: 20, color: 'white', flexDirection: 'row', display: 'flex', height: '100%' }}>
                <div style={{ height: 'calc(100vh - 180px)', paddingLeft: 0, display: 'flex', flexDirection: 'column', overflowY: 'scroll', width: 480, scrollBehavior: 'smooth', scrollbarColor: 'transparent' }}>
                    <p>LAST UPDATED - <b>{dayjs(data?.lastUpdated).format('YYYY-MM-DD HH:mm:ss')}</b></p>
                    <ul style={{ listStyleType: 'none', margin: 0, padding: 0 }}>
                        {
                            data.areas.map((area: API) => (
                                <li style={{ marginBottom: 25, display: 'flex', flexDirection: 'column', background: 'rgb(45,51,70)', padding: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto', }}>
                                    <h1 style={{ flex: 2, color: 'white' }}>{area.displayName}</h1>
                                    {/* <button onClick={() => showCities(area.displayName)}>show Cities</button> */}
                                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-around' }}>
                                        <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 20 }}><b>Confirmed:</b> <br /> {area.totalConfirmed?.toLocaleString('en-US') ?? 0}</p>
                                        <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 20 }}><b>Recovered:</b> <br />{area.totalRecovered?.toLocaleString('en-US') ?? 0}</p>
                                        <p style={{ flex: 1, color: 'red', fontSize: 20 }}><b>Deaths:</b> <br />{area.totalDeaths?.toLocaleString('en-US') ?? 0}</p>
                                    </div>
                                </li>
                            ))
                        }
                    </ul>
                </div>

                <div style={{
                    display: 'flex',
                    width: '100%',
                    position: 'relative',
                }}>
                    {showPopup.visible === true && <div style={{
                        width: 400,
                        height: 200,
                        position: 'absolute',
                        top: 25,
                        left: 25,
                        backgroundColor: 'white',
                        zIndex: 99999999,
                        padding: 15,
                        borderRadius: 8
                    }}>
                        {showPopup.country === true ? <h3 style={{ color: 'black' }}>{showPopup.region.displayName}</h3> : <span style={{ color: 'black', display: 'flex', flexDirection: 'row', alignItems: 'center' }}><h3>{showPopup.region.displayName} </h3> <label style={{ paddingLeft: 8 }}>{showPopup.country}</label></span>}
                        {showPopup.country === true && <p style={{ flex: 1, color: 'black', fontSize: 12 }}><b>Population:</b>  {getPopulation(showPopup.region.displayName).toLocaleString('en-US')}</p>}
                        {showPopup.country === true && <h3 style={{ flex: 1, color: 'black', fontSize: 12 }}><b>Population:</b>  {Number((showPopup.region.totalConfirmed * 100) / getPopulation(showPopup.region.displayName)).toFixed(2)}%</h3>}
                        <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 12 }}><b>Confirmed:</b>  {showPopup.region.totalConfirmed?.toLocaleString('en-US') ?? 0}</p>
                        <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 12 }}><b>Recovered:</b>{showPopup.region.totalRecovered?.toLocaleString('en-US') ?? 0}</p>
                        <p style={{ flex: 1, color: 'red', fontSize: 12 }}><b>Deaths:</b> {showPopup.region.totalDeaths?.toLocaleString('en-US') ?? 0}</p>
                    </div>}

                    <ReactMapGL
                        {...viewport}
                        width="100%"
                        height="calc(100vh - 120px)"
                        mapStyle="mapbox://styles/mapbox/dark-v9"
                        onViewportChange={setViewport}
                        mapboxApiAccessToken={MAPBOX_TOKEN}
                        onClick={() => setShowPopup({ visible: false, location: null })}
                    >

                        {useMemo(() => {
                            return data.areas.map(region => {
                                return <RedBubbleMarker width={10} region={region} />
                            })
                        }, [data])}

                        {useMemo(() => {
                            return data.areas.map(c => {
                                return c.areas.map(region => {
                                    return <RedBubbleMarker width={5} region={region} />
                                })
                            })
                        }, [data])}
                    </ReactMapGL>
                </div>
            </div>
        </Layout >
    )
}

const RenderCountriesMarkers = () => {

}



const RedBubbleMarker = ({ width, region }: { width: number, region: API }) => (
    <Marker key={region.displayName} longitude={region.long} latitude={region.lat} captureDrag={false}>
        <svg style={{
            cursor: 'pointer',
            fill: '#d00',
            stroke: 'none',
            width: width * 2,
            height: width * 2,
            transform: `translate(${-width / 2}px,${-width}px)`
        }}>
            <circle cx={width} cy={width} r={width} fill="red" style={{ fillOpacity: 3 }} />
        </svg>
    </Marker>
)

export async function getStaticProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()
    return { props: { data } }
}

export default React.memo(HomePage)
