import React, { useState } from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
import dayjs from 'dayjs'
import ReactMapGL from 'react-map-gl';

interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
}

interface IProps {
    data: API
}

const MAPBOX_TOKEN = 'pk.eyJ1IjoibWFydGlzOTAwIiwiYSI6ImNrMTZtazN4ODBhNGUzbW1yc245dG9nZzYifQ.T2ImKPTOuImP5pW11SXoWg';

function HomePage({ data }: IProps) {
    const [viewport, setViewport] = useState({
        width: 400,
        height: 400,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8
    });

    return (
        <Layout>
            <div style={{ padding: 20, color: 'white' }}>
                <ReactMapGL
                    {...viewport}
                    width="100vw"
                    height="100vh"
                    mapStyle="mapbox://styles/mapbox/dark-v9"
                    onViewportChange={setViewport}
                    mapboxApiAccessToken={MAPBOX_TOKEN}
                    >
                        {data.areas.map(country => {
                            country.areas.map()
                        })}
                    </ReactMapGL>
                {/* <h1>Total</h1>
                <h1><b>Confirmed:</b> {data.totalConfirmed?.toLocaleString('en-US')}</h1>
                <h1><b>Recovered:</b> {data.totalRecovered?.toLocaleString('en-US')}</h1>
                <h1><b>Deaths:</b> {data.totalDeaths?.toLocaleString('en-US')}</h1> */}
                <p>LAST UPDATED - <b>{dayjs(data.lastUpdated).format('YYYY-MM-DD HH:mm:ss')}</b></p>
                {data.areas.map((area: API) => (
                    <div style={{ background: 'rgb(45,51,70)', padding: 20, margin: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto', display: 'flex', alignItems: 'center', justifyContent: 'space-around' }}>
                        <h1 style={{ flex: 2, color: 'white' }}>{area.displayName}</h1>
                        <p style={{ flex: 1, color: 'rgb(252,184,44)', fontSize: 20 }}><b>Confirmed:</b> {area.totalConfirmed?.toLocaleString('en-US')}</p>
                        <p style={{ flex: 1, color: 'rgb(50,203,111)', fontSize: 20 }}><b>Recovered:</b> {area.totalRecovered?.toLocaleString('en-US')}</p>
                        <p style={{ flex: 1, color: 'red', fontSize: 20 }}><b>Deaths:</b> {area.totalDeaths?.toLocaleString('en-US')}</p>
                    </div>
                ))}
            </div>
        </Layout>
    )
}

export async function getStaticProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()
    return { props: { data } }
}

export default HomePage
