import React, { PureComponent } from 'react';
import { render } from 'react-dom';

import DeckGL, { TileLayer, BitmapLayer } from 'deck.gl';

export const INITIAL_VIEW_STATE = {
    latitude: 47.65,
    longitude: 7,
    zoom: 4.5,
    maxZoom: 20,
    bearing: 0
};

// https://wiki.openstreetmap.org/wiki/Slippy_map_tilenames#Tile_servers
const tileServer = 'https://c.tile.openstreetmap.org/';

export class App extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {};
        this._onHover = this._onHover.bind(this);
        this._renderTooltip = this._renderTooltip.bind(this);
    }

    _onHover({ x, y, sourceLayer, tile }) {
        this.setState({ x, y, hoveredObject: { sourceLayer, tile } });
    }

    _renderTooltip() {
        const { x, y, hoveredObject } = this.state;
        const { sourceLayer, tile } = hoveredObject || {};
        return (
            sourceLayer &&
            tile && (
                <div className="tooltip" style={{ left: x, top: y }}>
                    tile: x: {tile.x}, y: {tile.y}, z: {tile.z}
                </div>
            )
        );
    }

    _renderLayers() {
        const { autoHighlight = true, highlightColor = [60, 60, 60, 40] } = this.props;

        return [
            new TileLayer({
                pickable: true,
                onHover: this._onHover,
                autoHighlight,
                highlightColor,
                opacity: 1,
                // https://wiki.openstreetmap.org/wiki/Zoom_levels
                minZoom: 0,
                maxZoom: 19,

                renderSubLayers: props => {
                    const { x, y, z, bbox } = props.tile;
                    const { west, south, east, north } = bbox;

                    return new BitmapLayer(props, {
                        image: `${tileServer}/${z}/${x}/${y}.png`,
                        bounds: [west, south, east, north]
                    });
                }
            })
        ];
    }

    render() {
        const { viewState, controller = true } = this.props;

        return (
            <DeckGL
                layers={this._renderLayers()}
                initialViewState={INITIAL_VIEW_STATE}
                viewState={viewState}
                controller={controller}
            >
                {this._renderTooltip}
            </DeckGL>
        );
    }
}
