import React, { useState } from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
import dayjs from 'dayjs'
import ReactMapGL, { Marker } from 'react-map-gl';

interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
    lat?: number;
    long?: number;
}

interface IProps {
    data: API
}

const MAPBOX_TOKEN = 'pk.eyJ1IjoibWFydGlzOTAwIiwiYSI6ImNrMTZtazN4ODBhNGUzbW1yc245dG9nZzYifQ.T2ImKPTOuImP5pW11SXoWg';

function HomePage({ data }: IProps) {
    const [viewport, setViewport] = useState({
        width: 400,
        height: 400,
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8
    });

    return (
        <Layout>
            <div style={{ padding: 20, color: 'white', flexDirection: 'row' }}>
                <ReactMapGL
                    {...viewport}
                    width="80vw"
                    height="100vh"
                    mapStyle="mapbox://styles/mapbox/dark-v9"
                    onViewportChange={setViewport}
                    mapboxApiAccessToken={MAPBOX_TOKEN}
                >
                    {data.areas.map(region => {
                        return <Marker key={region.displayName} longitude={region.long} latitude={region.lat}>
                            <img src="https://img.icons8.com/plasticine/2x/map-pin.png" style={{ width: 50, height: 50 }} />
                            <div style={{ display: 'flex', flexDirection: 'column' }}>
                                <p>{region.displayName}</p>
                                <p>{region.totalDeaths?.toLocaleString('en-US')}</p>
                            </div>
                        </Marker>
                    })}
                </ReactMapGL>
                {/* <h1>Total</h1>
                <h1><b>Confirmed:</b> {data.totalConfirmed?.toLocaleString('en-US')}</h1>
                <h1><b>Recovered:</b> {data.totalRecovered?.toLocaleString('en-US')}</h1>
                <h1><b>Deaths:</b> {data.totalDeaths?.toLocaleString('en-US')}</h1> */}
            </div>
        </Layout>
    )
}

export async function getStaticProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()
    return { props: { data } }
}

export default HomePage
