import React from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
}

interface IProps {
    data: API
}

function HomePage({ data }: IProps) {

    return (
        <Layout>
            <div style={{ padding: 20 }}>
                <h1>Total</h1>
                <h1><b>Confirmed:</b> {data.totalConfirmed?.toLocaleString('en-US')}</h1>
                <h1><b>Recovered:</b> {data.totalRecovered?.toLocaleString('en-US')}</h1>
                <h1><b>Deaths:</b> {data.totalDeaths?.toLocaleString('en-US')}</h1>
                {data.areas.map((area: API) => (
                    <div style={{ background: 'black', padding: 20, margin: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto', display: 'flex', alignItems: 'center', justifyContent: 'space-around' }}>
                        <h1 style={{ width: 250 }}>{area.displayName}</h1>
                        <p style={{ color: 'yellow', fontSize: 20 }}><b>Confirmed:</b> {area.totalConfirmed?.toLocaleString('en-US')}</p>
                        <p style={{ color: 'green', fontSize: 20 }}><b>Recovered:</b> {area.totalRecovered?.toLocaleString('en-US')}</p>
                        <p style={{ color: 'red', fontSize: 20 }}><b>Deaths:</b> {area.totalDeaths?.toLocaleString('en-US')}</p>
                    </div>
                ))}
            </div>
        </Layout>
    )
}

export async function getStaticProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()
    return { props: { data } }
}

export default HomePage
