import { getSinglePost, getPosts } from '../api/posts'
import { useRouter } from 'next/router'
export default (props) => {
    const router = useRouter()

    if (!!!props.post) {
        return <div>Loading</div>
    }
    return <div dangerouslySetInnerHTML={{ __html: props.post.html }} />
}

export async function getStaticPaths() {
    const posts = await getPosts()
    return {
        // Only `/posts/1` and `/posts/2` are generated at build time
        paths: posts.reduce((sum, post) => {
            sum.push({ params: { id: post.slug } })
            return sum
        }, []),
        // Enable statically generating additional pages
        // For example: `/posts/3`
        fallback: false,
    }
}

export async function getStaticProps(context) {
    const post = await getSinglePost(context.params.id)
    return {
        props: { post }, // will be passed to the page component as props
    }
}
