import React, { useState, useEffect } from 'react'
import fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout'
import Link from 'next/link'
interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
}

interface IProps {
    data: API
}

function HomePage({ data }: IProps) {

    return (
        <Layout>
            <div style={{ padding: 20 }}>
                <h1>Total</h1>
                <h1><b>Confirmed:</b> {data.totalConfirmed}</h1>
                <h1><b>Recovered:</b> {data.totalRecovered}</h1>
                <h1><b>Deaths:</b> {data.totalDeaths}</h1>
                {data.areas.map((area: API) => (
                    <div style={{ background: 'black', padding: 20, margin: 20, borderRadius: 7, color: 'white', fontFamily: 'Roboto' }}>
                        <h1>{area.displayName}</h1>
                        <p style={{ color: 'yellow', fontSize: 20 }}><b>Confirmed:</b> {area.totalConfirmed}</p>
                        <p style={{ color: 'green', fontSize: 20 }}><b>Recovered:</b> {area.totalRecovered}</p>
                        <p style={{ color: 'red', fontSize: 20 }}><b>Deaths:</b> {area.totalDeaths}</p>
                    </div>
                ))}
            </div>
        </Layout>
    )
}

export async function getStaticProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()

    return { props: { data } }
}

export default HomePage
