import React, { useState, useEffect } from 'react'
import fetch from 'isomorphic-unfetch';

interface API {
    id: string;
    displayName: string;
    areas: API[];
    totalConfirmed: number;
    totalDeaths: number;
    totalRecovered: number;
    totalRecoveredDelta: number;
    totalDeathsDelta: number;
    totalConfirmedDelta: number;
    lastUpdated: Date;
}

interface IProps {
    data: API
}

function HomePage({ data }: IProps) {
    debugger
    return (
        <div>
            {data.areas.map((area: API) => (
                <span>
                    <h3>{area.displayName}</h3>
                    <h5>{area.totalConfirmed}</h5>
                </span>
            ))}
        </div>
    )
}

export async function getServerSideProps() {
    const res = await fetch('https://bing.com/covid/data');
    const data = await res.json()

    return { props: { data } }
}

export default HomePage
